-- converts a number in the range 0-1 to an integer in the range 0-255
local function roundcol (c)
  return math.floor((c * 255.0) + 0.5)
end

return {
  -- tries to read a popup on the screen, given a render2d event and the index of the first character.
  -- returns nil if no text is found.
  tryreadpopup = function (this, event, startindex)
    local text = ""
    local lastx, lasty, lastax, lastay
    local anytext = false
    for i = startindex, event:vertexcount(), event:verticesperimage() do
      local ax, ay, aw, ah, _, _ = event:vertexatlasdetails(i)
      local x, y = event:vertexxy(i)
      if aw == 6 and ah == 2 then goto continue end -- some kind of nbsp that comes up in player names
      if ah ~= 30 then break end
      if lastx and math.abs(x - lastx) < 2 and math.abs(y - lasty) < 2 and ax == lastax and ay == lastay then goto continue end
      local letter = this.popupmessagechars[event:texturedata(ax, ay + 10, aw * 4)]
      if type(letter) == "table" then
        letter = letter[event:texturedata(ax, ay + 22, aw * 4)]
      end
      if not letter then break end
      text = text .. letter
      anytext = true
      lastx = x
      lasty = y
      lastax = ax
      lastay = ay
      ::continue::
    end
    if not anytext then return nil end
    return text
  end,

  tryreadbuffdetails = function (this, event, startindex, buff, pxleft, pxtop, isbuff)
    local vertexcount = event:vertexcount()
    local verticesperimage = event:verticesperimage()
    local buffnumber = nil
    local parensnumber = nil
    local skipnumber = false

    local boxr = isbuff and 90 or 204
    local boxg = isbuff and 150 or 0
    local boxb = isbuff and 25 or 0

    for i = startindex, vertexcount, verticesperimage do
      if skipnumber then
        skipnumber = false
        goto continue
      end

      local ax, ay, aw, ah, _, _ = event:vertexatlasdetails(i)
      local u, v = event:vertexuv(i)
      if u == nil or v == nil then
        local x, y = event:vertexxy(i + 2)
        local fr, fg, fb = event:vertexcolour(i)
        if x == pxleft and y == pxtop and roundcol(fr) == boxr and roundcol(fg) == boxg and roundcol(fb) == boxb then
          buff.foundoncheckframe = true
          buff.active = true
          buff.number = buffnumber
          buff.parensnumber = parensnumber
          --print(string.format("buff name=%s number=%s", buff.name, buffnumber))
        end
        return nil
      end

      if ah <= 6 then return nil end
      local char = this.buffchars[event:texturedata(ax, ay + 6, aw * 4)]
      if type(char) == "table" then
        char = char[event:texturedata(ax, ay + 1, aw * 4)]
      end
      if not char then return nil end
      skipnumber = true

      if type(char) == "number" then
        if parensnumber ~= nil then
          parensnumber = char + (parensnumber * 10)
        elseif buffnumber ~= nil then
          buffnumber = char + (buffnumber * 10)
        else
          buffnumber = char
        end
      end

      if char == 'h' then
        if parensnumber ~= nil then
          parensnumber = parensnumber * 3600
        elseif buffnumber ~= nil then
          buffnumber = buffnumber * 3600
        end
      elseif char == 'm' then
        if parensnumber ~= nil then
          parensnumber = parensnumber * 60
        elseif buffnumber ~= nil then
          buffnumber = buffnumber * 60
        end
      elseif char == '(' and parensnumber == nil then
        parensnumber = 0
      end
      ::continue::
    end
  end,

